import 'dart:math';class Backoff {  final int maxAttempts;  final int baseDelay;  final int maxDelay;  int attempt = 0;  final Random _random = Random();  Backoff({    required this.maxAttempts,    this.baseDelay = 1,    this.maxDelay = 32,  });  Duration nextDelay() {    final delay =        min(baseDelay * pow(2, attempt) * (_random.nextDouble() + 1), maxDelay);    return Duration(seconds: delay.toInt());  }  bool shouldRetry() {    return attempt < maxAttempts;  }  void reset() {    attempt = 0;  }  void increment() {    attempt++;  }}